---Contact K SELECT DISTINCT CNORD# AS ORDER#, CNDISP AS DISPATCH, CNCALL AS CALL_ORDER, CNUNIT AS UNIT#, TRIM(SUBSTRING(CNREST, 9, 6)) AS TRAILER_NUM, CASE WHEN CNDATE IS NOT NULL AND CNTIME IS NOT NULL THEN TIMESTAMP( DATE( TO_DATE( DIGITS(CNDATE), 'YYYYDDD' ) ), SUBSTR(CNTIME, 1, 2) || ':' || SUBSTR(CNTIME, 3, 2) || ':00' ) ELSE NULL END AS EVENT_DTTM, CASE WHEN CNREST like '%P/U%' THEN 'ATTACH' WHEN CNREST like '%PU%' THEN 'ATTACH' WHEN CNREST like '%DROP%' THEN 'DROP' WHEN CNREST like '%DRP%' THEN 'DROP' END AS CALL_ACTION, CASE WHEN CNINIT ='24' and (CNREST like '%DROP%' or CNREST like '%DRP%') then 'Macro 24 Drop' WHEN CNINIT = '@FB' and (CNREST like '%DROP%' or CNREST like '%DRP%') then 'Freightbox Drop' WHEN CNINIT = '24' and (CNREST like '%P/U%' or CNREST like '%PU%') then 'Macro 24 Attach' WHEN CNINIT= '@FB' and (CNREST like '%P/U%' or CNREST like '%PU%') then 'Freightbox Attach' WHEN CNINIT not in ('24','@FB') and (CNREST like '%DROP%' or CNREST like '%DRP%') then 'K-Call Drop KNX User' WHEN CNINIT not in ('24','@FB') and (CNREST like '%DROP%' or CNREST like '%DRP%') then 'K-Call Attach KNX User' ELSE '' END AS CALL_TYPE FROM KTSFILE.CONTACT LEFT JOIN KTSFILE.TRCONTCT on KTSFILE.CONTACT.CNORD# = KTSFILE.TRCONTCT.TCORD and KTSFILE.CONTACT.CNDISP = KTSFILE.TRCONTCT.TCDISP WHERE CNDATE > '2025000' and CNCODE in ('K') and LEFT(CNORD#,1) not in ('C') and LEFT (CNUNIT,1) not in ('T') and CNORD# not in ('',' ') --AND (CNREST like '%P/U%' OR CNREST like '%PU%' OR CNREST like '%DROP%' OR CNREST like '%DRP%' ) AND CASE WHEN CNINIT ='24' and (CNREST like '%DROP%' or CNREST like '%DRP%') then 'Macro 24 Drop' WHEN CNINIT = '@FB' and (CNREST like '%DROP%' or CNREST like '%DRP%') then 'Freightbox Drop' WHEN CNINIT = '24' and (CNREST like '%P/U%' or CNREST like '%PU%') then 'Macro 24 Attach' WHEN CNINIT= '@FB' and (CNREST like '%P/U%' or CNREST like '%PU%') then 'Freightbox Attach' WHEN CNINIT not in ('24','@FB') and (CNREST like '%DROP%' or CNREST like '%DRP%') then 'K-Call Drop KNX User' WHEN CNINIT not in ('24','@FB') and (CNREST like '%DROP%' or CNREST like '%DRP%') then 'K-Call Attach KNX User' ELSE '' END !='' UNION ALL ---Mcleod SELECT DISTINCT 'McLeod' AS ORDER#, '00' AS DISPATCH, '99' AS CALL_ORDER, 'McLeod 3rd Party' AS UNIT#, TRAILERID as TRAILER_NUM, TRANSDATTM AS EVENT_DTTM, CASE WHEN TRIM(TRLRSTATUS)='H' THEN 'ATTACH' WHEN TRIM(TRLRSTATUS)='A' THEN 'DROP' END AS CALL_ACTION, CASE WHEN TRIM(TRLRSTATUS)='H' and TRIM(CRTUSER)='MCLD' THEN 'McLeod Attachment via UTStage' WHEN TRIM(TRLRSTATUS)='H' and TRIM(CRTUSER)='FBOX' THEN 'Freightbox Attachment via UTStage' WHEN TRIM(TRLRSTATUS)='H' and TRIM(CRTUSER)='UTXL' THEN 'UTXL Attachment via UTStage' WHEN TRIM(TRLRSTATUS)='H' THEN 'UNKNOWN Attachment via UTStage' WHEN TRIM(TRLRSTATUS)='A' and TRIM(CRTUSER)='MCLD' THEN 'McLeod Drop via UTStage' WHEN TRIM(TRLRSTATUS)='A' and TRIM(CRTUSER)='FBOX' THEN 'Freightbox Drop via UTStage' WHEN TRIM(TRLRSTATUS)='A' and TRIM(CRTUSER)='UTXL' THEN 'UTXL Drop via UTStage' WHEN TRIM(TRLRSTATUS)='A' THEN 'UNKNOWN Drop via UTStage' ELSE 'UNKNOWN UTStage Transaction' END as CALL_TYPE FROM SHARED.UTSTAGEHP WHERE SHARED.UTSTAGEHP.TRANSDATTM >= ('2025-01-01') AND TRANERR not in ('',' ') AND TRIM(TRLRSTATUS) IN('H' ,'A') UNION ALL ---D-CALL SELECT DISTINCT CONTACT.CNORD# AS ORDER#, CONTACT.CNDISP AS DISPATCH, CONTACT.CNCALL AS CALL_ORDER, CONTACT.CNUNIT AS UNIT#, TRCONTCT.TCTRLR AS TRAILER_NUM, CASE WHEN CONTACT.CNDATE IS NOT NULL AND CONTACT.CNTIME IS NOT NULL THEN TIMESTAMP( DATE( TO_DATE( DIGITS(CONTACT.CNDATE), 'YYYYDDD' ) ), SUBSTR(CONTACT.CNTIME, 1, 2) || ':' || SUBSTR(CONTACT.CNTIME, 3, 2) || ':00' ) ELSE NULL END AS EVENT_DTTM, unitlog.CALL_ACTION, CASE WHEN unitlog.CALL_ACTION = 'DROP' THEN 'D-Call Drop KNX User' WHEN unitlog.CALL_ACTION = 'ATTACH' THEN 'D-Call Attach KNX User' ELSE '' END AS CALL_TYPE FROM KTSFILE.CONTACT AS CONTACT LEFT JOIN KTSFILE.TRCONTCT AS TRCONTCT ON CONTACT.CNORD# = TRCONTCT.TCORD AND CONTACT.CNDISP = TRCONTCT.TCDISP LEFT JOIN KTSFILE.LOAD AS LOAD ON CONTACT.CNORD# = LOAD.DIODR# AND CONTACT.CNDISP = LOAD.DIDISP LEFT JOIN ( SELECT UNUNIT, UNTRL1, UNORD#, UNDISP, UNCNTD, UNCNTT, ROW_NUMBER() OVER (PARTITION BY UNORD# ORDER BY JOSEQN) AS Row_Num, CASE WHEN UNUNIT = LAG(UNUNIT) OVER (PARTITION BY UNORD# ORDER BY JOSEQN) AND UNTRL1 IS NULL AND LAG(UNTRL1) OVER (PARTITION BY UNORD# ORDER BY JOSEQN) IS NOT NULL THEN 'DROP' WHEN UNUNIT = LAG(UNUNIT) OVER (PARTITION BY UNORD# ORDER BY JOSEQN) AND UNTRL1 IS NOT NULL AND LAG(UNTRL1) OVER (PARTITION BY UNORD# ORDER BY JOSEQN) IS NULL THEN 'ATTACH' WHEN UNUNIT IS NOT NULL AND UNUNIT <> '' AND (LAG(UNUNIT) OVER (PARTITION BY UNORD# ORDER BY JOSEQN) IS NULL OR LAG(UNUNIT) OVER (PARTITION BY UNORD# ORDER BY JOSEQN) = '' OR ROW_NUMBER() OVER (PARTITION BY UNORD# ORDER BY JOSEQN) = 1) THEN 'ATTACH' WHEN (UNUNIT IS NULL OR UNUNIT = '') AND (LAG(UNUNIT) OVER (PARTITION BY UNORD# ORDER BY JOSEQN) IS NOT NULL AND LAG(UNUNIT) OVER (PARTITION BY UNORD# ORDER BY JOSEQN) <> '') THEN 'DETACH' ELSE 'OTHER' END AS CALL_ACTION FROM KTSCSTFIL.DSLUNTLOG WHERE JOJOB != 'MCPOSRPT' AND JOISO >= '20250101' ) AS unitlog ON CONTACT.CNORD# = unitlog.UNORD# AND CONTACT.CNDISP = unitlog.UNDISP AND CONTACT.CNDATE = unitlog.UNCNTD AND CONTACT.CNTIME = unitlog.UNCNTT WHERE CONTACT.CNDATE > '2025000' AND CONTACT.CNCODE IN ('D') AND CONTACT.CNCALL NOT IN ('01') AND LEFT(CONTACT.CNORD#, 1) NOT IN ('C') AND LEFT(CONTACT.CNUNIT, 1) NOT IN ('T') AND CONTACT.CNORD# NOT IN ('', ' ') AND unitlog.CALL_ACTION IN ('ATTACH', 'DROP');;;

